    };
    const fetchFreshData = async () => {
      try {
        tableData.value = [];
        let startTime = null;
        let endTime = null;
        if (searchTime.value != null && searchTime.value.length === 2) {
          const [startDate, endDate] = await Promise.all(searchTime.value.map(date => date.toISOString()));
          startTime = `${startDate.split('T')[0]}T00:00:00`;
          endTime = `${endDate.split('T')[0]}T23:59:59`;
        }
        // 请求参数
        const params = {
          current: currentPage.value,
          size: pageSize.value,
          startTime: startTime,
          endTime: endTime,
          ...rssNotificationQueryDto
        };
        const response = await proxy.$axios.post('rssNotification/query', params);
        const {
          data: responseData
        } = response;
        tableData.value = responseData.data;
        totalItems.value = responseData.total;
      } catch (error) {
        console.error('查询订阅通知信息异常:', error);
      }
    };
    const add = () => {
      // dialogOperation 在这个组件中没有定义，可能是遗留代码
    };
    const handleFilter = () => {
      currentPage.value = 1;
      fetchFreshData();
    };
    const handleFilterClear = () => {
      handleFilter();
    };
    const handleSizeChange = val => {
      pageSize.value = val;
      currentPage.value = 1;
      fetchFreshData();
    };
    const handleCurrentChange = val => {
      currentPage.value = val;
      fetchFreshData();
    };
    const messagePush = row => {
      data.value = {
        ...row
      };
    };
    const handleEdit = row => {
      // dialogOperation 在这个组件中没有定义，可能是遗留代码
      data.value = {
        ...row
      };
      cover.value = row.cover;
    };
    const handleDelete = row => {
      selectedRows.value.push(row);
      batchDelete();
    };

    // 生命周期
    onMounted(() => {
      fetchFreshData();
    });
    return {
      data,
      currentPage,
      cover,
      pageSize,
      totalItems,
      tableData,
      selectedRows,
      rssNotificationQueryDto,
      options,
      bookshelfOptions,
      searchTime,
      handleSelectionChange,
      batchDelete,
      resetQueryCondition,
      closeDialog,
      clearFormData,
      fetchFreshData,
      add,
      handleFilter,
      handleFilterClear,
      handleSizeChange,
