import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.map.js";
import { ref, reactive, onMounted, getCurrentInstance, nextTick } from 'vue';
import { getUploadUrl } from "@/utils/urlHelper.js";
import { Search, Warning, SuccessFilled, User, Plus, Camera, UserFilled, Medal, Message, Lock, TurnOff } from '@element-plus/icons-vue';
export default {
  components: {
    Search,
    Warning,
    SuccessFilled,
    User,
    Plus,
    Camera,
    UserFilled,
    Medal,
    Message,
    Lock,
    TurnOff
  },
  setup() {
    const {
      proxy
    } = getCurrentInstance();

    // 响应式数据
    const uploadUrl = ref(getUploadUrl());
    const userPwd = ref('');
    const userAvatar = ref('');
    const data = ref({});
    const filterText = ref('');
    const isAdmin = ref(false); // 是否是管理员标志，初始值为false
    const currentPage = ref(1);
    const pageSize = ref(10);
    const totalItems = ref(0);
    const dialogStatusOperation = ref(false);
    const dialogUserOperaion = ref(false); // 开关
    const isOperation = ref(false); // 开关-标识新增或修改
    const tableData = ref([]);
    const searchTime = ref([]);
    const selectedRows = ref([]);
    const status = ref(null);
    const userQueryDto = reactive({}); // 搜索条件
    const loginStatuList = ref([{
      value: null,
      label: '全部'
    }, {
      value: 0,
      label: '正常'
    }, {
      value: 1,
      label: '封号'
    }]);
    const wordStatuList = ref([{
      value: null,
      label: '全部'
    }, {
      value: 0,
      label: '正常'
    }, {
      value: 1,
      label: '禁言'
    }]);
    const rolesList = ref([{
      value: null,
      label: '全部'
    }, {
      value: 2,
      label: '用户'
    }, {
      value: 1,
      label: '管理员'
    }]);
    const cannel = () => {
      data.value = {};
      userAvatar.value = '';
      userPwd.value = '';
      dialogUserOperaion.value = false;
      dialogStatusOperation.value = false;
      isOperation.value = false;
      // 清理多余的遮罩层
      nextTick(() => {
        const modals = document.querySelectorAll('.v-modal');
        if (modals.length > 1) {
          for (let i = 1; i < modals.length; i++) {
            modals[i].parentNode.removeChild(modals[i]);
          }
        }
        // 移除body上的dialog样式
        document.body.classList.remove('el-popup-parent--hidden');
      });
    };
    const comfirmStatus = () => {
      const userUpdateDto = {
        id: data.value.id,
        isLogin: data.value.isLogin,
        userRole: isAdmin.value ? 1 : 2,
        isWord: data.value.isWord
      };
      proxy.$axios.put(`/user/backUpdate`, userUpdateDto).then(res => {
        if (res.data.code === 200) {
          proxy.$notify({
            duration: 1500,
            title: '信息修改',
            message: '修改成功',
            type: 'success'
          });
          cannel();
          fetchFreshData();
        }
      }).catch(error => {
        console.log("修改状态异常：" + error);
      });
    };

    // 修改用户状态
    const handleStatus = userData => {
      // 设置用户角色
      isAdmin.value = userData.userRole === 1;
      dialogStatusOperation.value = true;
      data.value = userData;
    };

    // 头像上传回调函数
    const handleAvatarSuccess = (res, file) => {
      proxy.$notify({
        duration: 1500,
        title: '头像上传',
        message: res.code === 200 ? '上传成功' : '上传失败',
        type: res.code === 200 ? 'success' : 'error'
      });
      // 上传成功则更新用户头像
      if (res.code === 200) {
        userAvatar.value = res.data;
      }
    };

    // 批量删除数据
    const batchDelete = async () => {
      if (!selectedRows.value.length) {
        proxy.$message(`未选中任何数据`);
        return;
      }
      const confirmed = await proxy.$swalConfirm({
        title: '删除用户数据',
        text: `删除后不可恢复，是否继续？`,
        icon: 'warning',
        showCancelButton: true,
        confirmButtonText: '确认删除',
        cancelButtonText: '取消',
        confirmButtonColor: '#f56c6c',
        cancelButtonColor: '#909399',
        customClass: {
          popup: 'custom-delete-popup',
          confirmButton: 'custom-delete-confirm-button',
          cancelButton: 'custom-delete-cancel-button',
          title: 'custom-delete-title',
          content: 'custom-delete-content',
          icon: 'custom-delete-icon'
        },
        buttonsStyling: true,
        iconColor: '#f56c6c',
        backdrop: `rgba(0,0,0,0.4)`,
        heightAuto: false,
        padding: '2em'
      });
      if (confirmed) {
        try {
          let ids = selectedRows.value.map(entity => entity.id);
          const response = await proxy.$axios.post(`/user/batchDelete`, ids);
          if (response.data.code === 200) {
            proxy.$notify({
              duration: 1000,
              title: '信息删除',
              message: '删除成功',
              type: 'success'
            });
            fetchFreshData();
            return;
          }
        } catch (error) {
          proxy.$message.error("用户信息删除异常：", error);
          console.error(`用户信息删除异常：`, error);
        }
      }
    };

    // 修改信息
    const updateOperation = async () => {
      if (userPwd.value !== '') {
        const pwd = proxy.$md5(proxy.$md5(userPwd.value));
        data.value.userPwd = pwd;
      } else {
        data.value.userPwd = null;
      }
      data.value.userAvatar = userAvatar.value;
      try {
        const response = await proxy.$axios.put('/user/backUpdate', data.value);
        if (response.data.code === 200) {
          proxy.$notify({
            duration: 1000,
            title: '信息修改',
            message: '修改成功',
            type: 'success'
          });
          cannel();
          fetchFreshData();
        }
      } catch (error) {
        console.error('修改出错:', error);
      }
    };

    // 信息新增
    const addOperation = async () => {
      //  密码处理
      if (userPwd.value !== '') {
        data.value.userPwd = proxy.$md5(proxy.$md5(userPwd.value));
      } else {
        data.value.userPwd = null;
      }
      data.value.userAvatar = userAvatar.value;
      try {
        const response = await proxy.$axios.post('/user/insert', data.value);
        if (response.data.code === 200) {
          proxy.$notify({
            duration: 1000,
            title: '信息新增',
            message: '新增成功',
            type: 'success'
          });
          cannel();
          fetchFreshData();
        } else {
          proxy.$notify({
            duration: 1000,
            title: '信息新增',
            message: response.data.msg,
            type: 'info'
          });
        }
      } catch (error) {
        console.error('信息新增出错:', error);
        proxy.$message.error('提交失败，请稍后再试！');
      }
    };
    const fetchFreshData = async () => {
      try {
        tableData.value = [];
        let startTime = null;
        let endTime = null;
        if (searchTime.value != null && searchTime.value.length === 2) {
          const [startDate, endDate] = await Promise.all(searchTime.value.map(date => date.toISOString()));
          startTime = `${startDate.split('T')[0]}T00:00:00`;
          endTime = `${endDate.split('T')[0]}T23:59:59`;
        }
        // 请求参数
        const params = {
          current: currentPage.value,
          size: pageSize.value,
          key: filterText.value,
          startTime: startTime,
          endTime: endTime,
          ...userQueryDto
        };
        const response = await proxy.$axios.post('/user/query', params);
        const {
          data: responseData
        } = response;
        tableData.value = responseData.data;
        totalItems.value = responseData.total;
      } catch (error) {
        proxy.$message.error("查询用户信息异常:", error);
        console.error('查询用户信息异常:', error);
      }
    };
    const add = () => {
      dialogUserOperaion.value = true;
    };
    const handleFilter = () => {
      currentPage.value = 1;
      fetchFreshData();
    };
    const handleFilterClear = () => {
      filterText.value = '';
      handleFilter();
    };
    const handleSizeChange = val => {
      pageSize.value = val;
      currentPage.value = 1;
      fetchFreshData();
    };
    const handleCurrentChange = val => {
      currentPage.value = val;
      fetchFreshData();
    };
    const handleEdit = row => {
      dialogUserOperaion.value = true;
      isOperation.value = true;
      row.userPwd = null;
      userAvatar.value = row.userAvatar;
      data.value = {
        ...row
      };
    };
    const handleDelete = row => {
      selectedRows.value.push(row);
      batchDelete();
    };

    // 生命周期
    onMounted(() => {
      fetchFreshData();
    });
    return {
      uploadUrl,
      userPwd,
      userAvatar,
      data,
      filterText,
      isAdmin,
      currentPage,
      pageSize,
      totalItems,
      dialogStatusOperation,
      dialogUserOperaion,
      isOperation,
      tableData,
      searchTime,
      selectedRows,
      status,
      userQueryDto,
      loginStatuList,
      wordStatuList,
      rolesList,
      cannel,
      comfirmStatus,
      handleStatus,
      handleAvatarSuccess,
      batchDelete,
      updateOperation,
      addOperation,
      fetchFreshData,
      add,
      handleFilter,
      handleFilterClear,
      handleSizeChange,
      handleCurrentChange,
      handleEdit,
      handleDelete
    };
  }
};
